CREATE TYPE "user_role" AS ENUM (
  'user',
  'admin'
);

CREATE TYPE "user_gender" AS ENUM (
  'male',
  'female'
);

CREATE TABLE "user_personal_data" (
                                    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                                    "user_id" integer,
                                    "firstname" varchar(255),
                                    "lastname" varchar(255),
                                    "gender" user_gender DEFAULT 'male',
                                    "created_at" timestamp DEFAULT (now()),
                                    "updated_at" timestamp DEFAULT null
);

CREATE TABLE "user_bio_data" (
                               "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                               "user_id" integer,
                               "weight" float(8),
                               "height" float(8),
                               "created_at" timestamp DEFAULT (now()),
                               "updated_at" timestamp DEFAULT null
);

CREATE TABLE "account" (
                         "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                         "user_id" integer,
                         "type" varchar(255),
                         "provider" varchar(255),
                         "providerAccountId" varchar(255),
                         "refresh_token" varchar(255),
                         "access_token" varchar(255),
                         "expires_at" integer,
                         "token_type" varchar(255),
                         "scope" varchar(255),
                         "id_token" varchar(255),
                         "session_state" varchar(255),
                         "created_at" timestamp DEFAULT (now()),
                         "updated_at" timestamp DEFAULT null
);

CREATE TABLE "user" (
                      "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                      "username" varchar(255) UNIQUE NOT NULL,
                      "email" varchar(255) UNIQUE NOT NULL,
                      "role" user_role NOT NULL DEFAULT 'user',
                      "created_at" timestamp DEFAULT (now()),
                      "updated_at" timestamp DEFAULT null
);

CREATE TABLE "water_intake" (
                              "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                              "user_id" integer,
                              "quantity" float(8),
                              "created_at" timestamp DEFAULT (now()),
                              "updated_at" timestamp DEFAULT null
);

CREATE TABLE "activity" (
                          "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                          "user_id" integer,
                          "name" varchar(255),
                          "calories_per_hour" float(8),
                          "duration_minutes" float(8),
                          "total_calories" float(8),
                          "created_at" timestamp DEFAULT (now()),
                          "updated_at" timestamp DEFAULT null
);

CREATE TABLE "exercise" (
                          "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                          "name" varchar(255),
                          "type" varchar(255),
                          "muscle" varchar(255),
                          "equipment" varchar(255),
                          "difficulty" varchar(255),
                          "instructions" varchar(255),
                          "video" varchar(255),
                          "created_at" timestamp DEFAULT (now()),
                          "updated_at" timestamp DEFAULT null
);

CREATE TABLE "workout_plan" (
                              "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                              "user_id" integer,
                              "workout_day_id" integer,
                              "description" varchar(255),
                              "notes" varchar(255),
                              "created_at" timestamp DEFAULT (now()),
                              "updated_at" timestamp DEFAULT null,
                              "rating" integer DEFAULT 10
);

CREATE TABLE "workout_day" (
                             "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                             "workout_plan_id" integer,
                             "exercise_id" integer,
                             "created_at" timestamp DEFAULT (now()),
                             "updated_at" timestamp DEFAULT null
);

CREATE TABLE "food" (
                      "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                      "name" varchar(255),
                      "calories" float(8),
                      "serving_size" float(8),
                      "protein" float(8),
                      "fat_total" float(8),
                      "fat_saturated" float(8),
                      "carbohydrates_total" float(8),
                      "fiber" float(8),
                      "sugar" float(8),
                      "sodium" float(8),
                      "potassium" float(8),
                      "cholesterol" float(8),
                      "created_at" timestamp DEFAULT (now()),
                      "updated_at" timestamp DEFAULT null
);

CREATE TABLE "meal_type" (
                           "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                           "user_id" integer,
                           "food_id" integer,
                           "meal_number" integer,
                           "meal_description" varchar(255),
                           "created_at" timestamp DEFAULT (now()),
                           "updated_at" timestamp DEFAULT null
);

CREATE TABLE "meal_plan" (
                           "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                           "user_id" integer,
                           "meal_type_id" integer,
                           "description" varchar(255),
                           "notes" varchar(255),
                           "total_calories" float(8),
                           "created_at" timestamp DEFAULT (now()),
                           "updated_at" timestamp DEFAULT null,
                           "rating" integer DEFAULT 10
);

CREATE TABLE "favourite" (
                           "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                           "user_id" integer,
                           "exercise_id" integer,
                           "activity_id" integer,
                           "food_id" integer,
                           "created_at" timestamp DEFAULT (now()),
                           "updated_at" timestamp DEFAULT null
);

CREATE TABLE "recipe" (
                        "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                        "user_id" integer,
                        "food_id" integer,
                        "description" varchar(255),
                        "created_at" timestamp DEFAULT (now()),
                        "updated_at" timestamp DEFAULT null
);

CREATE TABLE "recipe_user" (
                             "recipe_id" integer,
                             "user_id" integer,
                             "created_at" timestamp DEFAULT (now()),
                             "updated_at" timestamp DEFAULT null
);

CREATE TABLE "activity_user" (
                               "user_id" integer,
                               "activity_id" integer,
                               "created_at" timestamp DEFAULT (now()),
                               "updated_at" timestamp DEFAULT null
);

CREATE TABLE "meal_plan_meal_type" (
                                     "meal_plan_id" integer,
                                     "meal_type_id" integer,
                                     "created_at" timestamp DEFAULT (now()),
                                     "updated_at" timestamp DEFAULT null
);

CREATE TABLE "meal_plan_user" (
                                "meal_plan_id" integer,
                                "user_id" integer,
                                "created_at" timestamp DEFAULT (now()),
                                "updated_at" timestamp DEFAULT null
);

CREATE TABLE "workout_day_exercise" (
                                      "workout_day_id" integer,
                                      "exercise_id" integer,
                                      "created_at" timestamp DEFAULT (now()),
                                      "updated_at" timestamp DEFAULT null
);

CREATE TABLE "workout_day_plan" (
                                  "workout_day_id" integer,
                                  "workout_plan_id" integer,
                                  "created_at" timestamp DEFAULT (now()),
                                  "updated_at" timestamp DEFAULT null
);

CREATE TABLE "workout_plan_user" (
                                   "workout_plan_id" integer,
                                   "user_id" integer,
                                   "created_at" timestamp DEFAULT (now()),
                                   "updated_at" timestamp DEFAULT null
);

CREATE TABLE "water_intake_user" (
                                   "water_intake_id" integer,
                                   "user_id" integer
);

